INSERT INTO `oc_modification` VALUES (1,'Installation without FTP settings','no_ftp','karapuz (support@ka-station.com)','1.2.0','http://www.opencart.com/index.php?route=extension/extension/info&extension_id=19862','<modification> \n <id></id> \n <name>Installation without FTP settings</name> \n <code>no_ftp</code> \n <version>1.2.0</version> \n <author>karapuz (support@ka-station.com)</author> \n <link>http://www.opencart.com/index.php?route=extension/extension/info&amp;extension_id=19862</link> \n <file path=\"admin/controller/extension/installer.php\">\n <operation>\n <search index=\"0\"><![CDATA[$data[\'button_continue\'] =]]></search>\n <add position=\"after\"><![CDATA[//karapuz (no_ftp.ocmod.xml) \n $data[\'text_upload_without_ftp\'] = $this->language->get(\'text_upload_without_ftp\');\n $data[\'help_upload_without_ftp\'] = $this->language->get(\'help_upload_without_ftp\');\n $data[\'upload_without_ftp\'] = false;\n///karapuz (no_ftp.ocmod.xml) ]]></add>\n </operation>\n <operation>\n <search index=\"0\"><![CDATA[\'extension/installer/ftp\']]></search>\n <add position=\"replace\"><![CDATA[\'extension/installer/file_tp_wrapper\']]></add>\n </operation>\n <operation>\n <search index=\"0\"><![CDATA[function ftp(]]></search>\n <add position=\"before\"><![CDATA[//karapuz (no_ftp.ocmod.xml) \n static public function cpy($source, $dest) {\n if(is_dir($source)) {\n $dir_handle = opendir($source);\n while ($file = readdir($dir_handle)) {\n if ($file != \".\" && $file != \"..\") {\n if (is_dir($source . \"/\" . $file)) {\n if (!is_dir($dest . \"/\" . $file)) {\n mkdir($dest . \"/\" . $file);\n }\n self::cpy($source . \"/\" . $file, $dest . \"/\" . $file);\n } else {\n copy($source . \"/\" . $file, $dest . \"/\" . $file);\n }\n }\n }\n closedir($dir_handle);\n } else {\n copy($source, $dest);\n }\n }\n \n protected function copyUpload() {\n $json = array();\n \n $directory = DIR_UPLOAD . str_replace(array(\'../\', \'..\\\\\', \'..\'), \'\', $this->request->post[\'path\']) . \'/upload/\';\n\n if (!is_dir($directory)) {\n $json[\'error\'] = $this->language->get(\'error_directory\');\n return $json;\n }\n\n self::cpy($directory, dirname(DIR_APPLICATION));\n \n return $json;\n }\n \n /*\n This wrapper is used because some hostings block calls to URLs containing \'/ftp\'. It\n brokes installation process.\n */\n public function file_tp_wrapper() {\n return $this->ftp();\n }\n///karapuz (no_ftp.ocmod.xml) ]]></add>\n </operation>\n <operation>\n <search index=\"0\"><![CDATA[if (!$this->config->get(\'config_ftp_status\')]]></search>\n <add position=\"before\"><![CDATA[//karapuz (no_ftp.ocmod.xml) \n if (!empty($this->request->post[\'upload_without_ftp\'])) {\n $json = $this->copyUpload();\n $this->response->addHeader(\'Content-Type: application/json\');\n $this->response->setOutput(json_encode($json));\n return; \n }\n///karapuz (no_ftp.ocmod.xml) ]]></add>\n </operation>\n </file>\n <file path=\"admin/language/en-gb/extension/installer.php\">\n <operation>\n <search index=\"0\"><![CDATA[$_[\'text_clear\']]]></search>\n <add position=\"after\"><![CDATA[//karapuz (no_ftp.ocmod.xml) \n$_[\'text_upload_without_ftp\'] = \'Upload Without FTP\'; \n$_[\'help_upload_without_ftp\'] = \'When the checkbox is set, files from the archive will be unpacked to the store directory directly without connecting to it through FTP\';\n///karapuz (no_ftp.ocmod.xml) ]]></add>\n </operation>\n </file>\n <file path=\"admin/language/english/extension/installer.php\">\n <operation>\n <search index=\"0\"><![CDATA[$_[\'text_clear\']]]></search>\n <add position=\"after\"><![CDATA[//karapuz (no_ftp.ocmod.xml) \n$_[\'text_upload_without_ftp\'] = \'Upload Without FTP\';\n$_[\'help_upload_without_ftp\'] = \'When the checkbox is set, files from the archive will be unpacked to the store directory directly without connecting to it through FTP\';\n///karapuz (no_ftp.ocmod.xml) ]]></add>\n </operation>\n </file>\n <file path=\"admin/view/template/extension/installer.tpl\">\n <operation>\n <search index=\"0\"><![CDATA[<form class=\"form]]></search>\n <add position=\"after\"><![CDATA[<?php /* //karapuz (no_ftp.ocmod.xml) */?>\n <div class=\"form-group\">\n <label class=\"col-sm-2 control-label\" for=\"input-upload_without_ftp\"><span data-toggle=\"tooltip\" title=\"<?php echo $help_upload_without_ftp; ?>\"><?php echo $text_upload_without_ftp; ?></span></label>\n <div class=\"col-sm-10\">\n <div class=\"checkbox\">\n <label>\n <?php if ($upload_without_ftp) { ?>\n <input type=\"checkbox\" name=\"upload_without_ftp\" value=\"1\" checked=\"checked\" id=\"input-upload_without_ftp\" />\n <?php } else { ?>\n <input type=\"checkbox\" name=\"upload_without_ftp\" value=\"1\" id=\"input-upload_without_ftp\" />\n <?php } ?>\n &nbsp; </label>\n </div>\n </div>\n </div>\n<?php /* ///karapuz (no_ftp.ocmod.xml) */?>]]></add>\n </operation>\n <operation>\n <search index=\"0\"><![CDATA[timer = setInterval(]]></search>\n <add position=\"before\"><![CDATA[<?php /* //karapuz (no_ftp.ocmod.xml) */?>\n if (typeof(timer) != \'undefined\' && timer) {\n clearInterval(timer); \n }\n<?php /* ///karapuz (no_ftp.ocmod.xml) */?>]]></add>\n </operation>\n <operation>\n <search index=\"0\"><![CDATA[n next()]]></search>\n <add position=\"after\"><![CDATA[<?php /* //karapuz (no_ftp.ocmod.xml) */?>\n var custom_params = \'\';\n if ($(\'#input-upload_without_ftp\').prop(\'checked\')) {\n custom_params = \'&upload_without_ftp=1\';\n }\n<?php /* ///karapuz (no_ftp.ocmod.xml) */?>]]></add>\n </operation>\n <operation>\n <search index=\"0\"><![CDATA[\'path=\' + data.path]]></search>\n <add position=\"replace\"><![CDATA[\'path=\' + data.path + custom_params]]></add>\n </operation>\n </file>\n</modification>',1,'2016-12-12 08:48:26'),(2,'OCMod Editor','ocmod-editor','OpenCart Brasil','1.6','http://www.opencartbrasil.com.br','<?xml version=\"1.0\" encoding=\"utf-8\"?> \n<modification> \n <name>OCMod Editor</name> \n <code>ocmod-editor</code> \n <version>1.6</version> \n <author>OpenCart Brasil</author> \n <link>http://www.opencartbrasil.com.br</link> \n <file path=\"admin/controller/extension/modification.php\"> \n <operation> \n <search> \n <![CDATA[$data[\'refresh\'] = $this->url->link(\'extension/modification/refresh\', \'token=\' . $this->session->data[\'token\'] . $url, true);]]> \n </search> \n <add position=\"before\"> \n <![CDATA[$data[\'new\'] = $this->url->link(\'extension/modification_editor\', \'token=\' . $this->session->data[\'token\'] . $url, true);]]> \n </add> \n </operation> \n <operation> \n <search> \n <![CDATA[$data[\'modifications\'][] = array(]]> \n </search> \n <add position=\"after\"> \n <![CDATA[ \n \'edit\' => $this->url->link(\'extension/modification_editor\', \'token=\' . $this->session->data[\'token\'] . \'&modification_id=\' . $result[\'modification_id\'], true), \n \'download\' => $this->url->link(\'extension/modification_editor/download\', \'token=\' . $this->session->data[\'token\'] . \'&modification_id=\' . $result[\'modification_id\'], true), \n ]]> \n </add> \n </operation> \n <operation> \n <search> \n <![CDATA[$data[\'tab_log\'] = $this->language->get(\'tab_log\');]]> \n </search> \n <add position=\"after\"> \n <![CDATA[ \n $this->load->language(\'extension/modification_editor\'); \n $data[\'button_new\'] = $this->language->get(\'button_new\'); \n $data[\'button_edit\'] = $this->language->get(\'button_edit\'); \n $data[\'button_download\'] = $this->language->get(\'button_download\'); \n ]]> \n </add> \n </operation> \n <operation> \n <search index=\"1\"> \n <![CDATA[$this->response->redirect($this->url->link(\'extension/modification\', \'token=\' . $this->session->data[\'token\'] . $url, true));]]> \n </search> \n <add position=\"replace\"> \n <![CDATA[ \n if (isset($this->request->get[\'modification_editor\'])) { \n echo $this->session->data[\'success\']; \n exit; \n } else { \n $this->response->redirect($this->url->link(\'extension/modification\', \'token=\' . $this->session->data[\'token\'] . $url, true)); \n } \n ]]> \n </add> \n </operation> \n </file> \n \n <file path=\"admin/view/template/extension/modification.tpl\"> \n <operation> \n <search> \n <![CDATA[<div class=\"pull-right\">]]> \n </search> \n <add position=\"after\"> \n <![CDATA[<a href=\"<?php echo $new; ?>\" data-toggle=\"tooltip\" title=\"<?php echo $button_new; ?>\" class=\"btn btn-primary\"><i class=\"fa fa-plus\"></i></a>]]> \n </add> \n </operation> \n <operation> \n <search> \n <![CDATA[<?php if (!$modification[\'enabled\']) { ?>]]> \n </search> \n <add position=\"before\"> \n <![CDATA[ \n <a href=\"<?php echo $modification[\'edit\']; ?>\" data-toggle=\"tooltip\" title=\"<?php echo $button_edit; ?>\" class=\"btn btn-primary\"><i class=\"fa fa-pencil\"></i></a> \n <a href=\"<?php echo $modification[\'download\']; ?>\" data-toggle=\"tooltip\" title=\"<?php echo $button_download; ?>\" class=\"btn btn-success\"><i class=\"fa fa-cloud-download\"></i></a> \n ]]> \n </add> \n </operation> \n </file> \n</modification>',1,'2017-03-18 17:28:03'),(3,'So Social Login','So-Social-Login','Smartaddons','1.0','http://www.smartaddons.com/','<?xml version=\"1.0\" encoding=\"utf-8\"?>\n<modification>\n <name>So Social Login</name>\n <code>So-Social-Login</code>\n <version>1.0</version>\n <author>Smartaddons</author>\n <link>http://www.smartaddons.com/</link>\n \n <file path=\"catalog/view/theme/*/template/account/login.tpl\">\n <operation error=\"log\">\n <search ><![CDATA[<div class=\"well\">]]></search>\n <add position=\"replace\"><![CDATA[\n <div class=\"well col-sm-12\"> \n ]]></add>\n </operation>\n \n <operation error=\"log\">\n <search ><![CDATA[<input type=\"submit\" value=\"<?php echo $button_login; ?>\" class=\"btn btn-primary\" />]]></search>\n <add position=\"replace\"><![CDATA[\n <input type=\"submit\" value=\"<?php echo $button_login; ?>\" class=\"btn btn-primary pull-left\" /> \n ]]></add>\n </operation>\n \n <operation error=\"log\">\n <search ><![CDATA[</form>]]></search>\n <add position=\"after